KuroWeaponBase = KuroWeaponBase or class(NewRaycastWeaponBase)

-- More Weapon Stats Support
if Faker then
	ProjectileWeaponBase = ProjectileWeaponBase or class(NewRaycastWeaponBase)

	Faker.classes.KuroWeaponBase = KuroWeaponBase
	Faker:redo_class('KuroWeaponBase', 'ProjectileWeaponBase')
end

